DELETE FROM xls.handlers WHERE table_schema = 'xls' AND event_name = 'Actions' AND table_name IN ('columns', 'formats', 'handlers', 'objects', 'translations', 'workbooks', 'queries', 'users') and handler_name = 'SaveToDB Online Help';
