CREATE TABLE xls.columns (
    id serial NOT NULL
    , table_schema varchar(63) NOT NULL
    , table_name varchar(63) NOT NULL
    , column_name varchar(63) NOT NULL
    , ordinal_position integer NOT NULL
    , is_primary_key bit(1)
    , is_nullable bit(1)
    , is_identity bit(1)
    , is_computed bit(1)
    , column_default varchar(256)
    , data_type varchar(63) null
    , character_maximum_length integer
    , precision smallint
    , scale smallint
    , PRIMARY KEY (id)
);

CREATE UNIQUE INDEX ix_columns ON xls.columns USING BTREE (table_schema, table_name, column_name);

DROP INDEX IF EXISTS ix_handlers;

CREATE UNIQUE INDEX ix_handlers ON xls.handlers USING BTREE (table_schema, table_name, column_name, event_name, handler_schema, handler_name);

ALTER TABLE xls.formats ADD app varchar(50) NULL;

DROP INDEX IF EXISTS ix_formats;

CREATE UNIQUE INDEX ix_formats ON xls.formats USING BTREE (table_schema, table_name, app);

