CREATE OR REPLACE VIEW xls.queries
AS
SELECT
    t.table_schema
    , t.table_name
    , t.table_type
    , NULL AS table_code
    , NULL AS insert_procedure
    , NULL AS update_procedure
    , NULL AS delete_procedure
    , NULL AS procedure_type
FROM
    information_schema.tables t
WHERE
    NOT t.table_schema IN ('information_schema', 'pg_catalog', 'xls', 'savetodb_dev', 'savetodb_xls')
    AND NOT t.table_name LIKE 'xl_%'
UNION
SELECT
    r.routine_schema AS table_schema
    , r.routine_name AS table_name
    , r.routine_type AS table_type
    , NULL AS table_code
    , NULL AS insert_procedure
    , NULL AS update_procedure
    , NULL AS delete_procedure
    , NULL AS procedure_type
FROM
    information_schema.routines r
WHERE
    NOT r.routine_schema IN ('information_schema', 'pg_catalog', 'xls', 'savetodb_dev', 'savetodb_xls')
    AND r.routine_type = 'FUNCTION'
    AND NOT (
        r.routine_name LIKE '%_insert'
        OR r.routine_name LIKE '%_update'
        OR r.routine_name LIKE '%_delete'
        OR r.routine_name LIKE '%_merge'
        OR r.routine_name LIKE '%_change'
        OR r.routine_name LIKE 'xl_%'
    )
UNION ALL
SELECT
    o.table_schema
    , o.table_name
    , o.table_type
    , o.table_code
    , o.insert_object as insert_procedure
    , o.update_object as update_procedure
    , o.delete_object as delete_procedure
    , NULL as procedure_type
FROM
    xls.objects o
WHERE
    o.table_type IN ('CODE', 'HTTP', 'TEXT')
    AND o.table_schema IS NOT NULL
    AND o.table_name IS NOT NULL
    AND o.table_code IS NOT NULL
    AND NOT o.table_name LIKE 'xl_%'
ORDER BY
    table_schema
    , table_name;

CREATE OR REPLACE VIEW xls.users
AS
SELECT
    m.rolname AS user
    , rm.rolname AS role
FROM
    pg_catalog.pg_roles m
    LEFT OUTER JOIN (
        SELECT
            rm.member
            , r.rolname
        FROM
            pg_catalog.pg_auth_members rm
            INNER JOIN pg_catalog.pg_roles r ON r.oid = rm.roleid
        WHERE
            r.rolname IN ('xls_developers', 'xls_users', 'xls_formats')
    ) rm ON rm.member = m.oid
    INNER JOIN pg_catalog.pg_user u ON u.usesysid = m.oid
WHERE
    m.rolname NOT IN ('postgres')
UNION ALL
SELECT
    NULL AS user
    , r.rolname AS role
FROM
    pg_catalog.pg_roles r
    LEFT OUTER JOIN pg_catalog.pg_auth_members rm ON rm.roleid = r.oid
WHERE
    r.rolname IN ('xls_developers', 'xls_users', 'xls_formats')
    AND rm.roleid IS NULL;

CREATE OR REPLACE FUNCTION xls.xl_actions_set_role_permissions (
    )
    RETURNS void
    LANGUAGE plpgsql
AS $$
BEGIN

GRANT USAGE ON SCHEMA xls TO xls_users;
GRANT USAGE ON SCHEMA xls TO xls_formats;
GRANT USAGE ON SCHEMA xls TO xls_developers;

GRANT SELECT ON xls.columns             TO xls_users;
GRANT SELECT ON xls.formats             TO xls_users;
GRANT SELECT ON xls.handlers            TO xls_users;
GRANT SELECT ON xls.objects             TO xls_users;
GRANT SELECT ON xls.translations        TO xls_users;
GRANT SELECT ON xls.workbooks           TO xls_users;
GRANT SELECT ON xls.queries             TO xls_users;

GRANT SELECT, INSERT, UPDATE, DELETE ON xls.formats TO xls_formats;

GRANT USAGE, SELECT ON ALL SEQUENCES IN SCHEMA xls TO xls_formats;

GRANT SELECT, INSERT, UPDATE, DELETE ON TABLE xls.columns       TO xls_developers;
GRANT SELECT, INSERT, UPDATE, DELETE ON TABLE xls.objects       TO xls_developers;
GRANT SELECT, INSERT, UPDATE, DELETE ON TABLE xls.handlers      TO xls_developers;
GRANT SELECT, INSERT, UPDATE, DELETE ON TABLE xls.translations  TO xls_developers;
GRANT SELECT, INSERT, UPDATE, DELETE ON TABLE xls.formats       TO xls_developers;
GRANT SELECT, INSERT, UPDATE, DELETE ON TABLE xls.workbooks     TO xls_developers;
GRANT SELECT, INSERT, UPDATE, DELETE ON TABLE xls.queries       TO xls_developers;

GRANT USAGE, SELECT ON ALL SEQUENCES IN SCHEMA xls TO xls_developers;

GRANT SELECT ON xls.users TO xls_developers;

GRANT EXECUTE ON FUNCTION xls.xl_actions_set_role_permissions   TO xls_developers;

END
$$;


